package javaoperator;

public class ArithmaticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20 , b=3;
		//additional operator
		System.out.println("a=+b="+(a+b));
		//Subtraction operator
		System.out.println("a=-b="+(a-b));
		//multiplication operator
		System.out.println("a=*b="+(a*b));
		//boolean
		System.out.println("a%b="+(a%b));
				

	}

}
